// Section 8.2 Hartl Tutorial JS

let assert = require("assert"); // an assertion library that lets us assert that things are true in our tests
let Phrase = require("../index.js");  // importing the Phrase object itself

describe("Phrase", function() {

  describe("#palindrome", function() { // testing the Phrase#palindrome instance method

    it("should return false for a non-palindrome", function() {
      let nonPalindrome = new Phrase("apple");
      assert(!nonPalindrome.palindrome()); // assert that it is true that "apple" is NOT a palindrome 
    });

    it("should return true for a plain palindrome", function() {
      let plainPalindrome = new Phrase("racecar");
      assert(plainPalindrome.palindrome()); // assert "racecare" is palindrome
    });

    it("should return true for a mixed-case palindrome", function() { // Section 8.2 question
      let mixedCasePalindrome = new Phrase("Racecar");
      assert(mixedCasePalindrome.palindrome()); // assert "Racecare" is palindrome
    }); 
    
    it("should return true for a palindrome with punctuation", function() { // Section 8.3
      let punctuatedPalindrome = new Phrase("Madam, I'm Adam.");
      assert(punctuatedPalindrome.palindrome());
    });
  });

  describe("#letters", function() { // Section 8.3
    it("should return only letters", function() {
      let punctuatedPalindrome = new Phrase("Madam, I'm Adam.");
      assert.strictEqual(punctuatedPalindrome.letters(), "MadamImAdam");
    });
  });
});